
function tryJSON(body) {
    return new Promise(function tryJSON(resolve, reject) {
        try {
            resolve(JSON.parse(body));
        } catch (error) {
            reject({
                error: {
                    error_code: -2,
                    error_msg: 'Parse Error',
                    error: error,
                    body: body
                }
            });
        }
    });
}

module.exports = tryJSON;

